elf SH ON STK.ShelfId = SH.ShelfId AND SH.StatusId != 2 LEFT JOIN tblStore ST ON SH.StoreId = ST.StoreId AND ST.StatusId != 2 LEFT JOIN tblShelfCameraMapping SCM ON C.CameraId = SCM.CameraId AND SCM.StatusId = 1 LEFT JOIN tblShelf SH2 ON SCM.ShelfId = SH2.ShelfId AND SH2.StatusId = 1 LEFT JOIN tblStore ST2 ON ST2.StoreId = SH2.StoreId AND ST2.StatusId != 2) ) AS Camera) AS Camera
