mapper.update:
UPDATE tblCamera SET CameraName = #{cameraName},
ImageURL = #{imageURL},
IPAddress = #{ipAddress}, MACAddress = #{macAddress}, UpdatedTime = convert_tz(now(),@@session.time_zone,#{updatedTime}), RTSPString = #{rtspString} WHERE CameraId = #{cameraId}

UPDATE tblCamera
SET StatusId = #{statusId}, UpdatedTime = convert_tz(now(),@@session.time_zone,#{updatedTime})

    , ReasonInactive = #{reasonInactive}

WHERE CameraId = #{cameraId}

