mapper.select:
SELECT TR.totalOfRecord, CameraId, CameraName, ImageURL
FROM tblCamera,
     (SELECT COUNT(CameraId) AS totalOfRecord
     FROM tblCamera
     WHERE StatusId = 3 AND TypeDetect = #{typeDetect}

        AND CameraName like '%' #{cameraName} '%'
    ) TR
WHERE StatusId = 3 AND TypeDetect = #{typeDetect}

    AND CameraName like '%' #{cameraName} '%'

ORDER BY UpdatedTime DESC
LIMIT #{offSet}, #{fetchNext}

SELECT TR.totalOfRecord, Camera.CameraId, Camera.CameraName, Camera.ImageURL, Camera.IpAddress, Camera.StoreId, Camera.StoreName, Camera.UpdatedTime, Camera.StatusId, Camera.StatusName FROM (SELECT COUNT(TR.CameraId) AS totalOfRecord FROM (SELECT DISTINCT(Camera.CameraId) FROM (SELECT C.CameraId, C.CameraName, ST.StoreId, C.StatusId FROM tblCamera C JOIN tblStatus STT ON C.StatusId = STT.StatusId JOIN tblStackProductCameraMapping SPCM ON SPCM.CameraId = C.CameraId AND SPCM.StatusId = 1 JOIN tblStack STK ON SPCM.StackId = STK.StackId AND STK.StatusId = 1 LEFT JOIN tblShelf SH ON STK.ShelfId = SH.ShelfId AND SH.StatusId != 2 LEFT JOIN tblStore ST ON SH.StoreId = ST.StoreId AND ST.StatusId != 2 LEFT JOIN tblShelfCameraMapping SCM ON C.CameraId = SCM.CameraId AND SCM.StatusId = 1 LEFT JOIN tblShelf SH2 ON SCM.ShelfId = SH2.ShelfId AND SH2.StatusId = 1 LEFT JOIN tblStore ST2 ON ST2.StoreId = SH2.StoreId AND ST2.StatusId != 2 UNION SELECT C.CameraId, C.CameraName, ST2.StoreId, C.StatusId FROM tblCamera C JOIN tblStatus STT ON C.StatusId = STT.StatusId LEFT JOIN tblStackProductCameraMapping SPCM ON SPCM.CameraId = C.CameraId AND SPCM.StatusId = 1 LEFT JOIN tblStack STK ON SPCM.StackId = STK.StackId AND STK.StatusId = 1 LEFT JOIN tblShelf SH ON STK.ShelfId = SH.ShelfId AND SH.StatusId != 2 LEFT JOIN tblStore ST ON SH.StoreId = ST.StoreId AND ST.StatusId != 2 LEFT JOIN tblShelfCameraMapping SCM ON C.CameraId = SCM.CameraId AND SCM.StatusId = 1 LEFT JOIN tblShelf SH2 ON SCM.ShelfId = SH2.ShelfId AND SH2.StatusId = 1 LEFT JOIN tblStore ST2 ON ST2.StoreId = SH2.StoreId AND ST2.StatusId != 2 WHERE C.CameraId NOT IN (SELECT C.CameraId FROM tblCamera C JOIN tblStatus STT ON C.StatusId = STT.StatusId JOIN tblStackProductCameraMapping SPCM ON SPCM.CameraId = C.CameraId AND SPCM.StatusId = 1 JOIN tblStack STK ON SPCM.StackId = STK.StackId AND STK.StatusId = 1 LEFT JOIN tblShelf SH ON STK.ShelfId = SH.ShelfId AND SH.StatusId != 2 LEFT JOIN tblStore ST ON SH.StoreId = ST.StoreId AND ST.StatusId != 2 LEFT JOIN tblShelfCameraMapping SCM ON C.CameraId = SCM.CameraId AND SCM.StatusId = 1 LEFT JOIN tblShelf SH2 ON SCM.ShelfId = SH2.ShelfId AND SH2.StatusId = 1 LEFT JOIN tblStore ST2 ON ST2.StoreId = SH2.StoreId AND ST2.StatusId != 2) ) AS Camera
Camera.StoreId = #{storeId}
AND Camera.CameraName like '%' #{cameraName} '%'
AND Camera.StatusId = #{statusId}
) AS TR) TR,
(SELECT Camera.CameraId, Camera.CameraName, Camera.ImageURL, Camera.IpAddress, Camera.StoreId, Camera.StoreName, Camera.UpdatedTime, Camera.StatusId, Camera.StatusName FROM ( SELECT C.CameraId, C.CameraName, C.ImageURL, C.IPAddress, C.TypeDetect, ST.StoreId, ST.StoreName, C.UpdatedTime, C.StatusId, STT.StatusName FROM tblCamera C JOIN tblStatus STT ON C.StatusId = STT.StatusId JOIN tblStackProductCameraMapping SPCM ON SPCM.CameraId = C.CameraId AND SPCM.StatusId = 1 JOIN tblStack STK ON SPCM.StackId = STK.StackId AND STK.StatusId = 1 LEFT JOIN tblShelf SH ON STK.ShelfId = SH.ShelfId AND SH.StatusId != 2 LEFT JOIN tblStore ST ON SH.StoreId = ST.StoreId AND ST.StatusId != 2 LEFT JOIN tblShelfCameraMapping SCM ON C.CameraId = SCM.CameraId AND SCM.StatusId = 1 LEFT JOIN tblShelf SH2 ON SCM.ShelfId = SH2.ShelfId AND SH2.StatusId = 1 LEFT JOIN tblStore ST2 ON ST2.StoreId = SH2.StoreId AND ST2.StatusId != 2 UNION SELECT C.CameraId, C.CameraName, C.ImageURL, C.IPAddress, C.TypeDetect, ST2.StoreId, ST2.StoreName,C.UpdatedTime, C.StatusId, STT.StatusName FROM tblCamera C JOIN tblStatus STT ON C.StatusId = STT.StatusId LEFT JOIN tblStackProductCameraMapping SPCM ON SPCM.CameraId = C.CameraId AND SPCM.StatusId = 1 LEFT JOIN tblStack STK ON SPCM.StackId = STK.StackId AND STK.StatusId = 1 LEFT JOIN tblShelf SH ON STK.ShelfId = SH.ShelfId AND SH.StatusId != 2 LEFT JOIN tblStore ST ON SH.StoreId = ST.StoreId AND ST.StatusId != 2 LEFT JOIN tblShelfCameraMapping SCM ON C.CameraId = SCM.CameraId AND SCM.StatusId = 1 LEFT JOIN tblShelf SH2 ON SCM.ShelfId = SH2.ShelfId AND SH2.StatusId = 1 LEFT JOIN tblStore ST2 ON ST2.StoreId = SH2.StoreId AND ST2.StatusId != 2 WHERE C.CameraId NOT IN (SELECT C.CameraId FROM tblCamera C JOIN tblStatus STT ON C.StatusId = STT.StatusId JOIN tblStackProductCameraMapping SPCM ON SPCM.CameraId = C.CameraId AND SPCM.StatusId = 1 JOIN tblStack STK ON SPCM.StackId = STK.StackId AND STK.StatusId = 1 LEFT JOIN tblShelf SH ON STK.ShelfId = SH.ShelfId AND SH.StatusId != 2 LEFT JOIN tblStore ST ON SH.StoreId = ST.StoreId AND ST.StatusId != 2 LEFT JOIN tblShelfCameraMapping SCM ON C.CameraId = SCM.CameraId AND SCM.StatusId = 1 LEFT JOIN tblShelf SH2 ON SCM.ShelfId = SH2.ShelfId AND SH2.StatusId = 1 LEFT JOIN tblStore ST2 ON ST2.StoreId = SH2.StoreId AND ST2.StatusId != 2) ) AS Camera) AS Camera
