mapper.update:
UPDATE tblCamera SET CameraName = #{cameraName},
ImageURL = #{imageURL},
IPAddress = #{ipAddress}, UpdatedTime = convert_tz(now(),@@session.time_zone,#{updatedTime}) WHERE CameraId = #{cameraId}

UPDATE tblCamera
SET StatusId = #{statusId}, UpdatedTime = convert_tz(now(),@@session.time_zone,#{updatedTime})

    , ReasonInactive = #{reasonInactive}

WHERE CameraId = #{cameraId}

mapper.select:
SELECT StatusId
FROM tblCamera
WHERE CameraId = #{cameraId}

SELECT C.CameraId, C.CameraName, C.ImageURL, C.IPAddress, C.RTSPString, C.TypeDetect, C.UpdatedTime, C.CreatedTime, C.StatusId, STT.StatusName, C.ReasonInactive, STK.StackId, STK.Position, SH.ShelfId AS ShelfId, SH.ShelfName AS ShelfName, SPCM.AddedCameraTime, ST.StoreId, ST.StoreName, ST.ImageURL AS StoreImage FROM tblCamera C JOIN tblStatus STT ON C.StatusId = STT.StatusId LEFT JOIN tblStackProductCameraMapping SPCM ON SPCM.CameraId = C.CameraId AND SPCM.StatusId = 1 LEFT JOIN tblStack STK ON SPCM.StackId = STK.StackId AND STK.StatusId = 1 LEFT JOIN tblShelf SH ON STK.ShelfId = SH.ShelfId AND SH.StatusId != 2 JOIN tblStore ST ON SH.StoreId = ST.StoreId AND ST.StatusId != 2 AND ST.StoreId IS NOT NULL WHERE C.CameraId = #{cameraId}
AND ST.StoreId = #{storeId}
UNION ALL
SELECT C.CameraId, C.CameraName, C.ImageURL, C.IPAddress, C.RTSPString, C.TypeDetect, C.UpdatedTime, C.CreatedTime, C.StatusId, STT.StatusName, C.ReasonInactive, '' AS StackId, '' AS Position, SH2.ShelfId, SH2.ShelfName, SCM.AddedTime, ST2.StoreId, ST2.StoreName, ST2.ImageURL AS StoreImage FROM tblCamera C JOIN tblStatus STT ON C.StatusId = STT.StatusId LEFT JOIN tblShelfCameraMapping SCM ON C.CameraId = SCM.CameraId AND SCM.StatusId = 1 LEFT JOIN tblShelf SH2 ON SCM.ShelfId = SH2.ShelfId AND SH2.StatusId = 1 LEFT JOIN tblStore ST2 ON ST2.StoreId = SH2.StoreId AND ST2.StatusId != 2 AND ST2.StoreId IS NOT NULL WHERE C.CameraId = #{cameraId}
AND ST2.StoreId = #{storeId}
