mapper.select:
SELECT STA.StackId AS STACK_StackId, STA.Position AS STACK_Position, STA.CreatedTime AS STACK_CreatedTime, STA.UpdatedTime AS STACK_UpdatedTime, STA.ReasonInactive AS STACK_ReasonInactive, STA.ShelfId AS STACK_ShelfId, SHE.ShelfName AS STACK_ShelfName, STA.StatusId AS STACK_StatusId, ST.StatusName AS STACK_StatusName, TC.CameraId AS STACK_CameraId, TC.CameraName AS CAM_CameraName, TC.ImageURL AS CAM_ImageURL, TC.MACAddress AS CAM_MACAddress, TC.IPAddress AS CAM_IPAddress, TC.RTSPString AS CAM_RTSPString, TC.TypeDetect AS CAM_TypeDetect, TC.CreatedTime AS CAM_CreatedTime, TC.UpdatedTime AS CAM_UpdatedTime, TC.ReasonInactive AS CAM_ReasonInactive, TC.StatusId AS CAM_StatusId, STC.StatusName AS CAM_StatusName, TP.ProductId AS PRO_ProductId, TP.ProductName AS PRO_ProductName, TP.ImageURL AS PRO_ImageURL, TP.Description AS PRO_Description, TP.CreatedTime AS PRO_CreatedTime, TP.UpdatedTime AS PRO_UpdatedTime, TP.ReasonInactive AS PRO_ReasonInactive, TP.StatusId AS PRO_StatusId, STP.StatusName AS PRO_StatusName, CC.CategoryId, CN.CategoryName, TCOUNT.totalOfRecord FROM tblStack STA LEFT JOIN tblShelf SHE ON (STA.ShelfId = SHE.ShelfId) LEFT JOIN tblStatus ST ON (STA.StatusId = ST.StatusId) LEFT JOIN tblStackProductCameraMapping TSPCM ON (STA.StackId = TSPCM.StackId AND TSPCM.StatusId != 2) LEFT JOIN tblCamera TC ON (TSPCM.CameraId = TC.CameraId AND TSPCM.RemovedCameraTime IS NULL ) LEFT JOIN tblStatus STC ON (STC.StatusId = TC.StatusId) LEFT JOIN tblProduct TP ON (TSPCM.ProductId = TP.ProductId AND TSPCM.RemoveProductTime IS NULL) LEFT JOIN tblProductCategoryMapping CC ON(TP.ProductId = CC.ProductId) LEFT JOIN tblCategory CN ON(CC.CategoryId = CN.CategoryId) LEFT JOIN tblStatus STP ON (STP.StatusId = TP.StatusId), ( SELECT COUNT(STA.StackId) AS totalOfRecord FROM tblStack STA LEFT JOIN tblShelf SHE ON (STA.ShelfId = SHE.ShelfId) LEFT JOIN tblStackProductCameraMapping TSPCM ON (STA.StackId = TSPCM.StackId AND TSPCM.StatusId != 2) LEFT JOIN tblCamera TC ON (TSPCM.CameraId = TC.CameraId AND TSPCM.RemovedCameraTime IS NULL ) LEFT JOIN tblProduct TP ON (TSPCM.ProductId = TP.ProductId AND TSPCM.RemoveProductTime IS NULL) WHERE STA.ShelfId = #{shelfId}
AND STA.StatusId = #{statusId}
) TCOUNT WHERE STA.ShelfId = #{shelfId}
AND STA.StatusId = #{statusId}

SELECT A1.CategoryId, CN.CategoryName, A2.* FROM tblProductCategoryMapping A1 RIGHT JOIN ( SELECT STA.StackId AS STACK_StackId, STA.Position AS STACK_Position, STA.CreatedTime AS STACK_CreatedTime, STA.UpdatedTime AS STACK_UpdatedTime, STA.ReasonInactive AS STACK_ReasonInactive, STA.ShelfId AS STACK_ShelfId, SHE.ShelfName AS STACK_ShelfName, STA.StatusId AS STACK_StatusId, ST.StatusName AS STACK_StatusName, TC.CameraId AS STACK_CameraId, TC.CameraName AS CAM_CameraName, TC.ImageURL AS CAM_ImageURL, TC.MACAddress AS CAM_MACAddress, TC.IPAddress AS CAM_IPAddress, TC.RTSPString AS CAM_RTSPString, TC.TypeDetect AS CAM_TypeDetect, TC.CreatedTime AS CAM_CreatedTime, TC.UpdatedTime AS CAM_UpdatedTime, TC.ReasonInactive AS CAM_ReasonInactive, TC.StatusId AS CAM_StatusId, STC.StatusName AS CAM_StatusName, TP.ProductId AS PRO_ProductId, TP.ProductName AS PRO_ProductName, TP.ImageURL AS PRO_ImageURL, TP.Description AS PRO_Description, TP.CreatedTime AS PRO_CreatedTime, TP.UpdatedTime AS PRO_UpdatedTime, TP.ReasonInactive AS PRO_ReasonInactive, TP.StatusId AS PRO_StatusId, STP.StatusName AS PRO_StatusName, TCOUNT.totalOfRecord FROM tblStack STA LEFT JOIN tblShelf SHE ON (STA.ShelfId = SHE.ShelfId) LEFT JOIN tblStatus ST ON (STA.StatusId = ST.StatusId) LEFT JOIN tblStackProductCameraMapping TSPCM ON (STA.StackId = TSPCM.StackId AND TSPCM.StatusId != 2) LEFT JOIN tblCamera TC ON (TSPCM.CameraId = TC.CameraId AND TSPCM.RemovedCameraTime IS NULL ) LEFT JOIN tblStatus STC ON (STC.StatusId = TC.StatusId) LEFT JOIN tblProduct TP ON (TSPCM.ProductId = TP.ProductId AND TSPCM.RemoveProductTime IS NULL ) LEFT JOIN tblStatus STP ON (STP.StatusId = TP.StatusId), ( SELECT COUNT(STA.StackId) AS totalOfRecord FROM tblStack STA LEFT JOIN tblShelf SHE ON (STA.ShelfId = SHE.ShelfId ) LEFT JOIN tblStackProductCameraMapping TSPCM ON (STA.StackId = TSPCM.StackId AND TSPCM.StatusId != 2) WHERE SHE.StoreId = #{storeId} AND TSPCM.ProductId = #{productId}
AND STA.StatusId = #{statusId}
) TCOUNT WHERE SHE.StoreId = #{storeId} AND TSPCM.ProductId = #{productId}
AND STA.StatusId = #{statusId}
LIMIT #{offSet}, #{fetchNext}) A2 ON (A1.ProductId = A2.PRO_ProductId) LEFT JOIN tblCategory CN ON(A1.CategoryId = CN.CategoryId)

SELECT COUNT(StackId) AS TotalOfRecord
FROM tblStack SS
         LEFT JOIN tblShelf TS ON (TS.ShelfId = SS.ShelfId)
WHERE SS.StackId = #{stackId}
  AND TS.StoreId = #{storeId}

SELECT COUNT(ShelfId) AS TotalOfRecord
FROM tblShelf SS
WHERE SS.ShelfId = #{shelfId}
  AND SS.StoreId = #{storeId}

SELECT COUNT(CameraId) AS TotalOfRecord
FROM tblCamera
WHERE CameraId = #{cameraId}

SELECT COUNT(ProductId) AS TotalOfRecord
FROM tblProduct
WHERE ProductId = #{productId}

SELECT COUNT(StackProductCameraMappingId) AS TotalOfRecord
FROM tblStackProductCameraMapping
WHERE ProductId = #{productId}
    AND StackId = #{stackId}
    AND StatusId = 3

SELECT COUNT(StackProductCameraMappingId) AS TotalOfRecord
FROM tblStackProductCameraMapping
WHERE CameraId = #{cameraId}
  AND StackId = #{stackId}
  AND StatusId = 1
