mapper.select:
SELECT TS.ShelfCameraMappingId
FROM tblShelfCameraMapping TS
    LEFT JOIN tblCamera TC ON (TC.CameraId = TS.CameraId)
WHERE TC.MACAddress = #{macAddress}
AND TS.AddedTime <= #{startedTime}
AND (CASE WHEN TS.RemovedTime IS NULL THEN convert_tz(now(),@@session.time_zone,'+07:00')
    ELSE TS.RemovedTime END)  >= #{endedTime}
ORDER BY TS.AddedTime DESC
LIMIT 1

SELECT TS.StackProductCameraMappingId
FROM tblStackProductCameraMapping TS
    LEFT JOIN tblCamera TC ON (TC.CameraId = TS.CameraId)
WHERE TC.MACAddress = #{macAddress}
  AND TS.AddedCameraTime <= #{startedTime}
  AND (CASE WHEN TS.RemovedCameraTime IS NULL THEN convert_tz(now(),@@session.time_zone,'+07:00')
            ELSE TS.RemovedCameraTime END)  >= #{endedTime}
ORDER BY TS.AddedCameraTime DESC
LIMIT 1

SELECT COUNT(TV.VideoId) AS TOTALRECORD
FROM tblVideo TV
         LEFT JOIN tblShelfCameraMapping TSCM ON (TSCM.ShelfCameraMappingId = TV.ShelfCameraMappingId)
         LEFT JOIN tblCamera TC ON (TSCM.CameraId = TC.CameraId)
WHERE TV.StartedTime = #{startedTime}
  AND TC.MACAddress = #{macAddress}

SELECT COUNT(TV.VideoId) AS TOTALRECORD
FROM tblVideo TV
         LEFT JOIN tblStackProductCameraMapping TSCM ON (TSCM.StackProductCameraMappingId = TV.StackProductCameraMappingId)
         LEFT JOIN tblCamera TC ON (TSCM.CameraId = TC.CameraId)
WHERE TV.StartedTime = #{startedTime}
  AND TC.MACAddress = #{macAddress}

SELECT TV.VideoId, TV.VideoName, TV.VideoURL, TV.StartedTime, TV.EndedTime, TV.StatusId, TV.CreatedTime AS VID_CreatedTime, TV.UpdatedTime AS VID_UpdatedTime, TS.ShelfId, TS.ShelfName, TT.StoreId, TT.StoreName, THS.HotspotId, THS.StartedTime, THS.EndedTime, THS.TotalPeople, THS.Description, THS.CreatedTime, THS.UpdatedTime, TTTT.totalOfRecord FROM tblVideo TV LEFT JOIN tblShelfCameraMapping TSCM ON (TSCM.ShelfCameraMappingId = TV.ShelfCameraMappingId) LEFT JOIN tblShelf TS ON (TS.ShelfId = TSCM.ShelfId) LEFT JOIN tblStore TT ON (TT.StoreId = TS.StoreId) LEFT JOIN tblHotSpot THS ON (TSCM.ShelfCameraMappingId = THS.ShelfCameraMappingId AND TV.StartedTime = THS.StartedTime), ( SELECT COUNT(TV.VideoId) AS totalOfRecord FROM tblVideo TV LEFT JOIN tblShelfCameraMapping TSCM ON (TSCM.ShelfCameraMappingId = TV.ShelfCameraMappingId) LEFT JOIN tblShelf TS ON (TS.ShelfId = TSCM.ShelfId) LEFT JOIN tblStore TT ON (TT.StoreId = TS.StoreId) WHERE TS.ShelfId IS NOT NULL AND TV.StartedTime >= #{dayStart} AND TV.EndedTime <= #{dayEnd}
AND TS.StoreId = #{storeId}
AND TS.ShelfId = #{shelfId}
) TTTT WHERE TS.ShelfId IS NOT NULL AND TV.StartedTime >= #{dayStart} AND TV.EndedTime <= #{dayEnd}
AND TS.StoreId = #{storeId}
AND TS.ShelfId = #{shelfId}
ORDER BY TV.StartedTime DESC

SELECT TV.VideoId, TV.VideoName, TV.VideoURL, TV.StartedTime, TV.EndedTime, TV.StatusId, TV.CreatedTime AS VID_CreatedTime, TV.UpdatedTime AS VID_UpdatedTime, TS.ShelfId, TS.ShelfName, TT.StoreId, TT.StoreName, TSCM.ProductId, THS.EmotionId, THS.StartedTime, THS.EndedTime, THS.Angry, THS.Disgust, THS.Fear, THS.Happy, THS.Sad, THS.Surprise, THS.Neutral, THS.Description, THS.CreatedTime, THS.UpdatedTime, TTTT.totalOfRecord FROM tblVideo TV LEFT JOIN tblStackProductCameraMapping TSCM ON (TSCM.StackProductCameraMappingId = TV.StackProductCameraMappingId) LEFT JOIN tblStack TTS ON (TTS.StackId = TSCM.StackId) LEFT JOIN tblShelf TS ON (TS.ShelfId = TTS.ShelfId) LEFT JOIN tblStore TT ON (TT.StoreId = TS.StoreId) LEFT JOIN tblEmotion THS ON (TSCM.StackProductCameraMappingId = THS.StackProductCameraMappingId AND TV.StartedTime = THS.StartedTime), ( SELECT COUNT(TV.VideoId) AS totalOfRecord FROM tblVideo TV LEFT JOIN tblStackProductCameraMapping TSCM ON (TSCM.StackProductCameraMappingId = TV.StackProductCameraMappingId) LEFT JOIN tblStack TTS ON (TTS.StackId = TSCM.StackId) LEFT JOIN tblShelf TS ON (TS.ShelfId = TTS.ShelfId) LEFT JOIN tblStore TT ON (TT.StoreId = TS.StoreId) WHERE TSCM.ProductId = #{productId}
AND TS.StoreId = #{storeId}
AND TV.StartedTime >= #{dayStart} AND TV.EndedTime <= #{dayEnd} ) TTTT WHERE TSCM.ProductId = #{productId}
AND TS.StoreId = #{storeId}
AND TV.StartedTime >= #{dayStart} AND TV.EndedTime <= #{dayEnd} ORDER BY TV.StartedTime DESC
